 dataStore.edit {
 val current = it[SCHEDULED_REMINDER_IDS] ?: emptySet()
 it[SCHEDULED_REMINDER_IDS] = current - id
 }
 }

 suspend fun clearPreferences() {
 dataStore.edit { it.clear() }
 }

