@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun HomeScreen(
 viewModel: HomeViewModel = hiltViewModel()
) {
 val remindersState = viewModel.reminders.collectAsState()
 val reminders = remindersState.value
 val showDialog = remember { mutableStateOf(false) }
 val editReminder = remember { mutableStateOf<Reminder?>(null) }
 val userId = FirebaseAuth.getInstance().currentUser?.uid.orEmpty()

 Scaffold(
 floatingActionButton = {
 FloatingActionButton(onClick = {
 editReminder.value = null; showDialog.value = true
 }) {
 Icon(Icons.Default.Add, contentDescription = "Add Reminder")
 }
 }
 ) { innerPadding ->
 Box(modifier = Modifier.fillMaxSize()) {
 LazyColumn(
 contentPadding = innerPadding,
 modifier = Modifier.fillMaxSize()
 ) {
 items(reminders, key = { it.id }) { reminder ->
 SwipeToDismissReminderCard(
 reminder = reminder,
 onEdit = {
 editReminder.value = it
 showDialog.value = true
 },
 onDelete = {
 viewModel.deleteReminder(it.id)
 }
 )
 }
 }
 if (showDialog.value) {
 AddReminderDialog(
 onSave = { reminder ->
 if (editReminder.value == null) {
 viewModel.addReminder(reminder)
 } else {
 viewModel.updateReminder(reminder)
 }
 showDialog.value = false
 editReminder.value = null
 },
 onCancel = {
 showDialog.value = false
 editReminder.value = null
 },
 userId = userId,
 initialReminder = editReminder.value
 )
 }
 }
 }
}

@Composable
fun SwipeToDismissReminderCard(
 reminder: Reminder,
 onEdit: (Reminder) -> Unit,
 onDelete: (Reminder) -> Unit
) {
 val dismissState = rememberDismissState()
 if (dismissState.isDismissed(DismissDirection.EndToStart)) {
 onDelete(reminder)
 }
 if (dismissState.isDismissed(DismissDirection.StartToEnd)) {
 onEdit(reminder)
 }
 SwipeToDismiss(
 state = dismissState,
 background = {
 val color = when (dismissState.dismissDirection) {
 DismissDirection.StartToEnd -> Color.Yellow
 DismissDirection.EndToStart -> Color.Red
 else -> Color.Transparent
 }
 Box(
 modifier = Modifier
 .fillMaxSize()
 .background(color),
 contentAlignment = when (dismissState.dismissDirection) {
 DismissDirection.StartToEnd -> Alignment.CenterStart
 DismissDirection.EndToStart -> Alignment.CenterEnd
 else -> Alignment.Center
 }
 ) {
 Text(
 text = when (dismissState.dismissDirection) {
 DismissDirection.StartToEnd -> "Edit"
 DismissDirection.EndToStart -> "Delete"
 else -> ""
 },
 modifier = Modifier.padding(24.dp),
 color = Color.White,
 style = MaterialTheme.typography.titleMedium
 )
 }
 },
 dismissContent = {
 ReminderCard(reminder)
 },
 directions = setOf(DismissDirection.StartToEnd, DismissDirection.EndToStart)
 )
}

@Composable
fun ReminderCard(reminder: Reminder) {
 Card(
 modifier = Modifier
 .fillMaxWidth()
 .padding(8.dp),
 shape = RoundedCornerShape(16.dp),
 elevation = CardDefaults.cardElevation(defaultElevation = 4.dp)
 ) {
 Row(
 modifier = Modifier
 .fillMaxWidth()
 .padding(16.dp),
 verticalAlignment = Alignment.CenterVertically
 ) {
 if (!reminder.imageUrl.isNullOrEmpty()) {
 AsyncImage(
 model = reminder.imageUrl,
 contentDescription = null,
 modifier = Modifier
 .size(56.dp)
 .clip(CircleShape)
 .background(Color.Gray),
 contentScale = ContentScale.Crop
 )
 Spacer(modifier = Modifier.width(16.dp))
 }
 Column(Modifier.weight(1f)) {
