@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun NumberPicker(value: Int, range: IntRange, onValueChange: (Int) -> Unit, label: String) {
 Column(horizontalAlignment = Alignment.CenterHorizontally) {
 Text(label, style = MaterialTheme.typography.bodySmall)
 Row(verticalAlignment = Alignment.CenterVertically) {
 IconButton(onClick = {
 val next = if (value > range.first) value - 1 else range.last
 onValueChange(next)
 }) { Text("-") }
 Text(value.toString().padStart(2, '0'), style = MaterialTheme.typography.titleLarge)
 IconButton(onClick = {
 val next = if (value < range.last) value + 1 else range.first
 onValueChange(next)
 }) { Text("+") }
 }
 }
